const modalButtons=document.querySelectorAll(".modal"),closeModal=document.querySelectorAll(".close_modal");$(document).ready(function(){$(".slider").slick({dots:!0,infinite:!1,speed:300,slidesToShow:1,centerMode:!0,autoplay:!0,autoplaySpeed:3e3,responsive:[{breakpoint:1024,settings:{slidesToShow:1,dots:!0}},{breakpoint:600,settings:{slidesToShow:1,centerPadding:"10px"}},{breakpoint:480,settings:{slidesToShow:1,centerPadding:"10px"}}]}),modalButtons.forEach(o=>o.addEventListener("click",function(){console.log("this"),$(this).siblings(".open_modal").css("display","block")})),closeModal.forEach(o=>o.addEventListener("click",function(){console.log("this"),$(this).closest(".open_modal").css("display","none")}))});