const modal=document.querySelectorAll(".modal");$(document).ready(function(){$(".slider").slick({dots:!0,infinite:!1,speed:300,slidesToShow:1,centerMode:!0,autoplay:!0,autoplaySpeed:3e3,responsive:[{breakpoint:1024,settings:{slidesToShow:1,dots:!0}},{breakpoint:600,settings:{slidesToShow:1,centerPadding:"10px"}},{breakpoint:480,settings:{slidesToShow:1,centerPadding:"10px"}}]}),buttons.forEach(e=>e.addEventListener("click",function(){$(this).siblings(".expanded").css("display","block")}))});